package com.example.hateoas.config;import org.springframework.context.annotation.Configuration;import org.springframework.hateoas.config.EnableHypermediaSupport;import org.springframework.hateoas.config.EnableHypermediaSupport.HypermediaType;import org.springframework.hateoas.support.WebStack;import org.springframework.web.reactive.config.WebFluxConfigurer;@Configuration@EnableHypermediaSupport(stacks = WebStack.WEBFLUX, type = {HypermediaType.HAL, HypermediaType.HAL_FORMS})public class HalFormsApplication implements WebFluxConfigurer {}